class Men:
    def __init__(self, jobs, name, age):
        self.jobs = jobs
        self.name = name
        self.age = age

world = Men(jobs='programmer', name='Doni', age=39)
print(vars(world)) # vars выводит все данные : {'jobs': 'programmer', 'name': 'Doni', 'age': 39}

class Men:
    def __init__(self, jobs, name, age):
        self.jobs = jobs
        self.name = name
        self.age = age
    def stop(self):
        print('Мужчина остановился')

world = Men(jobs='programmer', name='Doni', age=39)
world.stop() # мужсчина остановился

