class BankAccount:
    def __init__(self, name, balance=0):
        self.balance = balance
        self.name = name
    def deposit(self, cash_amount):
        self.balance += cash_amount


    def cash(self, cash_amount):
        self.balance -= cash_amount


    def my_balance(self):
        return self.balance

sasha = BankAccount('Саша')
print(sasha.my_balance())
sasha.deposit(10)
print(sasha.my_balance())
sasha.cash(6)
print(sasha.my_balance())


